from bs4 import BeautifulSoup
import re

def parse_problem_set(html: str):
    soup = BeautifulSoup(html, "html.parser")

    problems = []

    for headline in soup.find_all("span", class_="mw-headline"):
        problem_title = headline.get_text(strip=True)
        problem_content_parts = []
        answers = {}

        for sibling in headline.parent.find_next_siblings():
            if sibling.name and sibling.name.startswith("h"):
                break  # stop at next problem

            if sibling.name == "p":
                # Check if this <p> contains answer choices
                img_with_answers = sibling.find("img", alt=re.compile(r"\\textbf"))
                if img_with_answers:
                    answer_string = img_with_answers["alt"]
                    matches = re.findall(r"\\textbf{\((\w)\)}~([^\\]+)", answer_string)
                    for letter, val in matches:
                        answers[letter] = val.strip()
                    continue  # don't include this <p> in the question text

                # Otherwise, it's part of the problem statement
                text_parts = []
                for elem in sibling.contents:
                    if elem.name == "img" and elem.has_attr("alt"):
                        text_parts.append(elem["alt"])  # inline LaTeX
                    elif isinstance(elem, str):
                        text_parts.append(elem.strip())
                text = " ".join([t for t in text_parts if t])
                if text:
                    problem_content_parts.append(text)

        problems.append({
            "title": problem_title,
            "question": " ".join(problem_content_parts),
            "answers": answers
        })

    return problems


